mapper.select:
SELECT ai.id as aiId, ai.sort as aiSort, ai.`code` as aiCode, ai.`name` as aiName, img.file_url as aiIcon, ai.recharge_mode as aiRechargeMode, ai.`status` as aiStatus, ai.modify_time as modifyTime, ac.`name` as acName, `at`.`name` as atName, acp.`name` as acpName FROM app_info ai LEFT JOIN app_cate ac ON ai.cate_id = ac.id LEFT JOIN app_type `at` ON ai.type_id = `at`.id LEFT JOIN app_company acp ON ai.acp_id = acp.id LEFT JOIN sys_images as img ON img.type = 1 AND img.rel_id = ai.id WHERE 1 = 1 AND ai.del_flag = 0
AND ai.cate_id = #{cateId}
AND ai.acp_id = #{acpId}
AND ai.type_id = #{typeId}
AND ai.recharge_mode = #{rechargeMode}
and ai.status = #{status}
AND ( ai. NAME LIKE CONCAT('%',#{query},'%') OR ai.code LIKE CONCAT('%',#{query},'%'))
ORDER BY ai.sort desc , ai.create_time DESC

mapper.update:
UPDATE
app_info
SET status =#{status}
WHERE
id =#{aiId}
