mapper.select:
SELECT dai.ai_id AS id, ai.sort AS sort, ai.`name` AS name, img.file_url AS icon, ai.first_letter as firstLetter, ai.spll_letter as spllLetter, ai.is_reco as isReco, ai.is_hot as isHot FROM dealer_app_info as dai JOIN app_info as ai ON ai.id = dai.ai_id LEFT JOIN sys_images as img ON img.type = 1 AND img.rel_id = ai.id WHERE 1=1 AND ai.del_flag = 0 AND dai.status=1 and ai.status=1 AND dai.dealer_id = #{dealerId}
AND ai.cate_id = #{cateId}
AND ai.is_reco = 1
ORDER BY ai.sort ASC
