mapper.select:
select is_custom as isCustom
from dealer_app_price_type
where ai_id=#{aiId}
and dealer_id=#{dealerId}
and apt_id=#{aptId}

mapper.delete:
delete from
dealer_app_price_type
where
dealer_id=#{dealerId}
and ai_id=#{aiId}

select.if:
SELECT aa.aiId, aa.appStatus, aa.dealerAppStatus, aa.`CODE`, aa.appName, aa.cateName, aa.typeName, aa.companyName, aa.discount, aa.supNo FROM ( SELECT ai.id AS aiId, ai. STATUS AS appStatus, dai. STATUS AS dealerAppStatus, ai. CODE, ai. NAME AS appName, f_get_app_cate_name (ai.cate_id) AS cateName, f_get_app_type_name (ai.type_id) AS typeName, f_get_app_company_name (ai.acp_id) AS companyName, dapi.discount, dapi.sup_no AS supNo, ai.acp_id, ai.cate_id, ai.type_id FROM app_info ai LEFT JOIN dealer_app_info dai ON dai.ai_id = ai.id LEFT JOIN ( SELECT GROUP_CONCAT(sup_no SEPARATOR ',') AS sup_no, GROUP_CONCAT(discount SEPARATOR ',') AS discount, ai_id, dealer_id FROM dealer_app_price_info GROUP BY ai_id, dealer_id ) AS dapi ON dapi.ai_id = ai.id AND dapi.dealer_id = dai.dealer_id WHERE 1 = 1 AND ai.del_flag = 0 AND dai.dealer_id = #{dealerId} UNION ALL SELECT ai.id AS aiId, ai. STATUS AS appStatus, ai. CODE AS dealerAppStatus, ai. CODE, ai. NAME AS appName, f_get_app_cate_name (ai.cate_id) AS cateName, f_get_app_type_name (ai.type_id) AS typeName, f_get_app_company_name (ai.acp_id) AS companyName, ai. CODE AS discount, ai. CODE AS supNo, ai.acp_id, ai.cate_id, ai.type_id FROM app_info ai WHERE 1 = 1 AND ai.del_flag = 0 AND NOT EXISTS ( SELECT dai.ai_id FROM dealer_app_info dai WHERE dai.ai_id = ai.id AND dai.dealer_id = #{dealerId} ) ) aa WHERE 1 = 1
and aa. dealerAppStatus = #{status}
AND aa.cate_id =#{cateId}
and aa.acp_id=#{acpId}
and aa.type_id=#{typeId}
and (aa.appName LIKE CONCAT('%',#{searchName},'%') or aa.code LIKE CONCAT('%',#{searchName},'%') or aa.supNo LIKE CONCAT('%',#{searchName},'%') )

SELECT aa.aiId, aa.appStatus, aa.dealerAppStatus, aa.`CODE`, aa.appName, aa.cateName, aa.typeName, aa.companyName, aa.discount, aa.supNo FROM ( SELECT ai.id as aiId, ai.status as appStatus, dai. status as dealerAppStatus, ai. CODE, ai. NAME AS appName, f_get_app_cate_name (ai.cate_id) AS cateName, f_get_app_type_name (ai.type_id) AS typeName, f_get_app_company_name (ai.acp_id) AS companyName, dapi.discount, dapi.sup_no as supNo, ai.acp_id, ai.cate_id, ai.type_id FROM app_info ai LEFT JOIN dealer_app_info dai ON dai.ai_id = ai.id LEFT JOIN ( SELECT GROUP_CONCAT(sup_no SEPARATOR ',') AS sup_no, GROUP_CONCAT(discount SEPARATOR ',') AS discount, ai_id, dealer_id FROM dealer_app_price_info GROUP BY ai_id, dealer_id ) AS dapi ON dapi.ai_id = ai.id AND dapi.dealer_id = dai.dealer_id WHERE 1 = 1 AND ai.del_flag = 0 AND dai.dealer_id = #{dealerId} ) aa WHERE 1=1
and aa. dealerAppStatus = #{status}
AND aa.cate_id =#{cateId}
and aa.acp_id=#{acpId}
and aa.type_id=#{typeId}
and (aa.appName LIKE CONCAT('%',#{searchName},'%') or aa.code LIKE CONCAT('%',#{searchName},'%') or aa.supNo LIKE CONCAT('%',#{searchName},'%') )

SELECT aa.aiId, aa.appStatus, aa.dealerAppStatus, aa.`CODE`, aa.appName, aa.cateName, aa.typeName, aa.companyName, aa.discount, aa.supNo FROM ( SELECT ai.id as aiId, ai. status AS appStatus, ai.code as dealerAppStatus, ai. code, ai. NAME AS appName, f_get_app_cate_name (ai.cate_id) AS cateName, f_get_app_type_name (ai.type_id) AS typeName, f_get_app_company_name (ai.acp_id) AS companyName, ai. CODE AS discount, ai. CODE AS supNo, ai.acp_id, ai.cate_id, ai.type_id FROM app_info ai WHERE 1 = 1 AND ai.del_flag = 0 AND NOT EXISTS ( SELECT dai.ai_id FROM dealer_app_info dai WHERE dai.ai_id = ai.id AND dai.dealer_id = #{dealerId} ) ) aa WHERE 1=1
AND aa.cate_id =#{cateId}
and aa.acp_id=#{acpId}
and aa.type_id=#{typeId}
and (aa.appName LIKE CONCAT('%',#{searchName},'%') or aa.code LIKE CONCAT('%',#{searchName},'%') or aa.supNo LIKE CONCAT('%',#{searchName},'%') )

mapper.select:
SELECT ai.id AS appId, ai. NAME AS appName, img.file_url AS appIcon, ai.type_id as typeId, ai.game_id as gameId, ai.recharge_mode AS rechargeMode, ap.content AS content, aa.main_name AS accMainName, aa.main_regular AS accMainRegular, aa.main_msg AS accMainMsg, aa.sub_name AS accSubName, aa.sub_regular AS accSubRegular, aa.sub_msg AS accSubMsg FROM dealer_app_info dai LEFT JOIN app_info ai ON dai.ai_id = ai.id LEFT JOIN app_accttype aa ON ai.acct_id = aa.id LEFT JOIN app_page ap ON ai.id = ap.ai_id LEFT JOIN sys_images as img ON img.type = 1 AND img.rel_id = ai.id WHERE dai.dealer_id = #{dealerId} AND dai. STATUS = 1 and dai.ai_id in
#{item}
and ai.status=1

mapper.delete:
DELETE
FROM
dealer_app_price_info
WHERE
ai_id =#{aiId}
AND dealer_id =#{dealerId}
