mapper.delete:
UPDATE sys_permission
SET del_flag = 1
WHERE id = #{id}

mapper.select:
SELECT id, unique_key AS uniqueKey, parent_key AS parentKey, permission_name AS permissionName, permission_type AS permissionType, leaf_node AS leafNode FROM sys_permission WHERE unique_key IN (
#{uniqueKey}
) AND permission_status = 0 AND del_flag = 0

SELECT id, unique_key AS uniqueKey, parent_key AS parentKey, permission_name AS permissionName, permission_type AS permissionType, leaf_node AS leafNode, permission_status AS permissionStatus, description FROM sys_permission WHERE id IN
#{id}
AND permission_status = 0 AND del_flag = 0
