mapper.select:
SELECT o.id orderId, o.code, o.price, o.status, o.total_fee totalFee, o.price_type_name priceTypeName, o.trade_fee tradeFee, o.create_time createTime, app.id appId, app.name appName, img.file_url appIcon FROM `order` o INNER JOIN app_info app ON o.app_id = app.id LEFT JOIN sys_images AS img ON img.type = 1 AND img.rel_id = app.id WHERE o.del_flag = 0 AND o.user_id = #{userId}
AND o.status = #{status}
AND o.status IN (2,3,4)
AND o.status != 4
ORDER BY o.create_time DESC

SELECT o.id, o.code, o.out_trade_no, d.idnum dealerNum, d. NAME dealerName, o.account, app. NAME appName, o.price, o.discount, o.total_fee, o.trade_fee, o.dealer_price, o.trade_method, o. status, o.sup_status, o.pay_time FROM `order` o INNER JOIN app_info app ON o.app_id = app.id INNER JOIN dealer d ON o.dealer_id = d.id WHERE 1 = 1
AND o.create_time >= #{startDate} AND o.create_time < #{endDate}
AND o.sup_status = #{supStatus}
AND o.status = #{status}
AND o.status IN (2,3,4)
AND o.status != 4
AND ( o.code LIKE concat(concat('%',#{key}),'%') OR o.out_trade_no LIKE concat(concat('%',#{key}),'%') OR d.idnum LIKE concat(concat('%',#{key}),'%') )
ORDER BY o.modify_time
